import {PerfilWords} from '../style/style'
export default function Contras ({contras}) {
    const idperfil = localStorage.getItem('id')
//    console.log(contras)
    function getNames(id) {
        if(id === "p1") {
            return "Thiago"
        }
        if(id === "p2") {
            return "Rick"
        }
        if(id === "p3") {
            return "Joao"
        }
        if(id === "p4") {
            return "Daniel"
        }
        if(id === "p5") {
            return "Vitor"
        }
    }
    const players = Object.entries(contras)
   return (
       <div >
           { players.map((element) => {
               let _key
               console.log(element[1])
               if(element[1][0].id_player == idperfil) {
                   _key =  0
               }else {
                   _key = 1
               }
            
        return  (
                <div>
                    <div style={{display: "flex", alignItems: "center", justifyContent: "center"}}> <PerfilWords>
                            {getNames(element[0])} {element[1][_key].vitorias} / {element[1][_key].derrotas} | {element[1][_key].empates}
                            </PerfilWords>
                    </div>
                    
                </div>
            )
        })}
       </div>
   )
     
}
