import {ContainerLastMatches, FontNameAndRanking,NameLastMatchsWinner, NameLastMatchLoser, ContainerNameLastMatch, BoxLastMatchs} from '../style/style'
import axios from 'axios'
import { useEffect, useState } from 'react'


function LastMatchs () {

    const [lastmatchs, setLastmatchs] = useState([])
    const [empateBorder, setEmpateBorder] = useState("blue")
    
    useEffect(() => {
        getLastMatchs()
    },[])

   
    async function getLastMatchs () {
        const { data } = await axios.get('http://localhost:3333/lastmatchs')
        let arrayreverse = data.reverse()
        let cortado = arrayreverse.slice(0,10)
        setLastmatchs(prevState => ([...lastmatchs, ...cortado]))
    }

    return (
        <ContainerLastMatches>
             <FontNameAndRanking>
                Ãšltimas partidas
            </FontNameAndRanking>
            <BoxLastMatchs>
           <div>
            {lastmatchs.map((element, i) => {
               if(element.empate == 1) {
                    return (
                            <ContainerNameLastMatch>
                                <NameLastMatchsWinner colorwinner={empateBorder}>{element.p1} </NameLastMatchsWinner>
                            </ContainerNameLastMatch>
                    )
               }else {
                    <ContainerNameLastMatch>
                        <NameLastMatchsWinner colorwinner={null}>{element.p1} </NameLastMatchsWinner>
                    </ContainerNameLastMatch>
               }
                // return (
                //             <ContainerNameLastMatch>
                //                 <NameLastMatchsWinner colorwinner={empateBorder}>{element.p1} </NameLastMatchsWinner>
                //             </ContainerNameLastMatch>
                //         )
            })}
           </div>
           <div>
            {lastmatchs.map((element,i) => {
                return (
                            <ContainerNameLastMatch>
                                <NameLastMatchLoser colorLoser={empateBorder}> {element.p2}</NameLastMatchLoser>
                            </ContainerNameLastMatch>
                        )
                })}
            </div>
            </BoxLastMatchs>
        </ContainerLastMatches>
    )
}


export default LastMatchs
