import {ContainerLastMatches, FontNameAndRanking,NameLastMatchsWinner, NameLastMatchLoser, ContainerNameLastMatch, BoxLastMatchs} from '../style/style'
import axios from 'axios'
import { useEffect, useState } from 'react'

const data = []

function LastMatchs () {   
    const [lastmatchs, setLastmatchs] = useState([])
    const [empateBorder, setEmpateBorder] = useState(null)
    async function getLastMatchs () {
        const {data} = await axios.get('http://localhost:3333/lastmatchs')
        const reverse = data.reverse()
        data.push(...reverse)
        console.log(data)
       
    }
    useEffect(() => {
        getLastMatchs()
    },[])


    return (
        <ContainerLastMatches>
             <FontNameAndRanking>
                Ãšltimas partidas
            </FontNameAndRanking>
            <BoxLastMatchs>
           <div>
            {data.map((element, i) => {
                    if(element.empate == 1 ) {
                        setEmpateBorder("blue")
                    }
                    while(i <= 9 ) {
                        return (
                        <ContainerNameLastMatch>
                            <NameLastMatchsWinner colorwinner={empateBorder}>{element.p1} </NameLastMatchsWinner>
                         </ContainerNameLastMatch>
                        )
                    } 
            })}
           </div>
           <div>
                {data.map((element,i) => {
                    while( i <= 9) {
                        return (
                            <ContainerNameLastMatch>
                                <NameLastMatchLoser>{element.p2}</NameLastMatchLoser>
                            </ContainerNameLastMatch>
                        )
                    }
                })}
            </div>
            </BoxLastMatchs>
        </ContainerLastMatches>
    )
}


export default LastMatchs
